import React, { useState } from 'react';
import './styles.css'
import axios from 'axios';
import UserService from '../service/UserService';
import { isVisible } from '@testing-library/user-event/dist/utils';


class LoginForm extends React.Component {

  constructor(props) {
    super(props);
  }

  async signInClick() {
    const username = document.getElementById('input-username').value;
    const pwd = document.getElementById('input-password').value;
    var result;
    var response;
    await UserService.checkUser(username, pwd).then((value) => {
      response = value.data;
    })
    return response;
  }

  render() {
    var showInput = false;
    return (
      <div className='main'>
        <header className='style-header'>Login</header>
        <input id='input-username' placeholder='Username' className='input-username input-basic'></input>
        <input id='input-password' type={'password'} placeholder='Password' className='input-password input-basic'></input>
        <button className='login-button' onClick={() => {
          const response = this.signInClick();
          if (response == 'error') {
            document.getElementById('error').classList.remove('hidden');
            document.getElementById('error').classList.add('visible');
            window.location.reload(false);
          } else {
            //TODO
            console.log('You are logged in!');
          }
        }}>Sign in</button>
        <h1 id='error' className='error-message'>Invalid username or password!</h1>
      </div >
    )
  }
}


export default LoginForm;
