import React from "react";
import LoginForm from "./components/LoginForm";
import "./App.css"
import { BrowserRouter as Router, Route, Routes, useNavigate } from "react-router-dom";
import LoggedInForm from "./components/LoggedInForm";


function App() {

  let isAuthenticated = localStorage.getItem('isLogged');

  let navigate = useNavigate();

  let routeChange = () => {
    navigate("/");
  }

  return (
    <div className="App">
      <Routes>
        <Route path="/" exact element={<LoginForm />}></Route>
        <Route
          path="/logged"
          render={(props) =>
            isAuthenticated ? <LoggedInForm /> : routeChange()
          }
        />
      </Routes>
    </div>
  );
}

export default App;
