import axios from "axios";
import React from "react";

const URL = "http://localhost:8080/users";

class UserService {

  checkUser(userName, password) {
    var result;
    axios.post(URL, {
      username: userName,
      pwd: password
    }).then((value) => result = value.data);
    return result;
  }
}

export default new UserService;
