import React from 'react';
import './styles.css'
import axios from 'axios';
import UserService from '../service/UserService';

class LoginForm extends React.Component {

  constructor(props) {
    super(props);
  }

  async signInClick() {
    const username = document.getElementById('input-username').value;
    const pwd = document.getElementById('input-password').value;
    var response;
    UserService.checkUser(username, pwd).then((value) => {
      if (value == 'error') {
        //TODO handle error
        console.log('error');
      }
      else response = value.data;
    }).finally(() => console.log(response));
  }

  render() {
    var showInput = false;

    return (
      <div className='main'>
        <header className='style-header'>Login</header>
        <input id='input-username' placeholder='Username' className='input-username input-basic'></input>
        <input id='input-password' type={'password'} placeholder='Password' className='input-password input-basic'></input>
        <button className='login-button' onClick={() => this.signInClick()}>Sign in</button>
      </div >
    )
  }
}


export default LoginForm;
