import React from 'react';
import './styles.css'
import logo from './show-password.png';


class LoginForm extends React.Component {

  constructor(props) {
    super(props);
  }

  render() {
    var showInput = false;

    return (
      <div className='main'>
        <header className='style-header'>Login</header>
        <input placeholder='Username' className='input-username input-basic'></input>
        <input type={showInput ? 'text' : 'password'} placeholder='Password' className='input-password input-basic'></input>
        <button className='login-button'>Sign in</button>
        <button className='show-password-button'></button>
      </div >
    )
  }
}


export default LoginForm;
